const { userId, topic, sections = [] } = $json.body;

if (!userId) throw new Error("Missing userId");
if (!topic) throw new Error("Missing topic");

const sectionsText = sections.join("، ");

const prompt = `
أنت باحث أكاديمي متخصص.

اكتب بحثًا علميًا باللغة العربية فقط بعنوان:
"${topic}"

التزم بالأقسام التالية فقط:
${sectionsText}

اكتب بأسلوب أكاديمي رسمي بدون HTML أو Markdown.
`;

return {
  userId,
  prompt
};

// Handle different response structures from AIML API
// Sometimes usage is at $json.usage, sometimes at response level
const response = $json;
const usage = response.usage || response.data?.usage || {};

// Extract token counts with fallbacks
const inputTokens = usage.prompt_tokens || 0;
const outputTokens = usage.completion_tokens || 0;
const totalTokens = usage.total_tokens || (inputTokens + outputTokens);

// Pricing for GPT-5-nano (AIML API)
const INPUT_PRICE_PER_1M = 0.105;
const OUTPUT_PRICE_PER_1M = 0.42;
const USD_TO_SAR = 3.75;
const MARKUP = 2;

// Calculate costs
const inputCostUSD = (inputTokens / 1_000_000) * INPUT_PRICE_PER_1M;
const outputCostUSD = (outputTokens / 1_000_000) * OUTPUT_PRICE_PER_1M;
const totalCostUSD = inputCostUSD + outputCostUSD;
const finalCostSAR = totalCostUSD * USD_TO_SAR * MARKUP;

if (finalCostSAR <= 0) {
  throw new Error("Invalid cost calculation");
}

// Extract text content
const text = response.choices?.[0]?.message?.content || response.text || '';

if (!text) {
  throw new Error('No text content found in API response');
}

return {
  userId: $node["Build Prompt"].json.userId, // Explicitly pass userId
  text: text,
  billing: {
    promptTokens: inputTokens,
    completionTokens: outputTokens,
    totalTokens,
    inputCostUSD,
    outputCostUSD,
    totalCostUSD,
    finalCostSAR,
    currency: "SAR",
    markup: MARKUP
  }
};

const prompt = $input.item.json.prompt;

return {
  json: {
    requestBody: JSON.stringify({
      model: "openai/gpt-5-nano-2025-08-07",
      messages: [{
        role: "user",
        content: prompt
      }],
      temperature: 0.7,
      max_tokens: 8000  // ← Changed from 2000 to 8000!
    })
  }
};

return {
  success: true,

  data: {
    text: $json.text,
    billing: $json.billing
  },

  balance: {
    before: $json.balance,
    after: $json.balance - $json.billing.finalCostSAR,
    currency: "SAR"
  },

  meta: {
    generatedAt: new Date().toISOString(),
    model: "gpt-5-nano",
    language: "ar",
    tokensUsed: $json.billing.totalTokens
  }
};

const balance = $json.firebaseResponse.balance;
const cost = $json.billing.finalCostSAR;

// Check if user has sufficient balance
if (balance < cost) {
  throw new Error(`Insufficient balance: ${balance} SAR < ${cost.toFixed(2)} SAR`);
}

return {
  ...$json,
  balance
};
